import axios from 'axios';
import * as React from 'react'

import EmployeeService from '../services/EmployeeService';
export default class Employee extends React.Component<any, any> {
    readonly url="http://localhost:8090/api/Employee/GetEmployees";
    constructor(props: any) {
        super(props);

        this.state = {
            data: null,
            isLoading: false,
            employees: [],
            persons:[]
        };
    };
    componentDidMount() {

        //     this.setState({ isLoading: true });
        //     EmployeeService.getEmployeeList().
        //     then(response => response.json())
        //    .then(data => this.setState({ employees: data }));
        const token = localStorage.getItem('access_token');
        this.setState({ isLoading: true });

        // fetch("http://localhost:8090/api/Employee/GetEmployees", {
        //     method: 'GET',
        //     headers: {
        //         'Content-Type': 'application/json',
        //         'Authorization': `Bearer ${token?.replace(/["]+/g, '')}`,
                
        //     },
        //     redirect: 'follow'
        // })
        //     .then(response => response.json())
        //     .then(data => this.setState({ employees: data, isLoading: false }))
        //     .catch(error => console.log('Authorization failed : ' + error.message));
        axios.get(this.url,{
            headers:{
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token?.replace(/["]+/g, '')}`,
            }
        })
        .then(res => {
          const persons = res.data;
          console.log(persons);
          this.setState({ persons });
        })
       
    }
    render() {
        const { employees, isLoading ,persons} = this.state;
        if (isLoading) {
            return <p>Loading ...</p>;
        }
        return (
            <div>
               
                 <ul>
                     {
                          this.state.persons
                          .map(person =>
                            <li key={person.id}>{person.name}</li>
                          )
                     }
                </ul>
            </div>
        )
    }

}
