import React, { useEffect, useState } from "react";
import { render } from "react-dom";
import employeeService from "../services/EmployeeService";


export default function Update(props) {
    const [name, setName] = useState("");
    const [company, setCompany] = useState("");
    const [month, setMonth] = useState(new Date());

   useEffect(()=>{
alert(props.id);

   });
    const handleDate = date => {
        setMonth(date);
    }
    const handleSubmit = (e) => {
        e.preventDefault();
        var emp = {
            employeeId: 0,
            employeeName: name,
            companyName: company,
            month: month,
        };
        employeeService.update(emp)
            .then(d => {
                alert('data updated successfulyy!');
                window.location.href='/employee';
            }).catch(error => { console.log(error) });

    }

    return (
        <div className="container">
            <h2>Create</h2>
            <form onSubmit={handleSubmit} >
                <div className="form-group">
                    <input type='text' className="form-control" placeholder="Name"
                        onChange={(e) => { setName(e.target.value) }} />
                </div>
                <div className="form-group">
                    <input type='text' className="form-control mt-2" placeholder="Company"
                        onChange={(e) => { setCompany(e.target.value) }} />
                </div>
                <div className="form-group">
                    <input type='date' className="form-control mt-2" placeholder="Date"
                        onChange={(e) => handleDate(e.target.value)} />
                </div>
                <div className="form-group">
                    <input type='submit' className="btn btn-primary mt-2" value='Save' />
                </div>
            </form>
        </div>
    )
}
const root=document.getElementById('root');
render(<Update/>,root);
