import React, { useState } from 'react';
import PropTypes from 'prop-types';
import './Login.css';
import User from './User';
import authService from '../services/authService';
import { Button } from 'react-bootstrap';

async function loginUser(user: User) {
  console.log('inside login')
  return authService.login(user);
}

export default function Login() {
  const [username, setUserName] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = async () => {
    var user = {
      user: username,
      password: password
    };
    // loginUser(user);

    let result = await fetch("http://localhost:8090/api/Auth", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(user)
    });

    const data = await result.json();
    let key=data.token;
    localStorage.setItem("access_token", JSON.stringify(key));
    
  }

  return (
    <div className="login-wrapper">
      <h1>Please Log In</h1>
      <form onSubmit={handleSubmit}>
        <div className='card'>
          <input type="text" className='form-control' placeholder='User Name' onChange={(e) => setUserName(e.target.value)} />
        </div>
        <div>
          <input type="text" className='form-control' placeholder='Password' onChange={(e) => setPassword(e.target.value)} />
        </div>
        <div>
          <button type="submit" className='btn btn-prinary'>Submit</button>
          <Button className='btn' variant='primary'>Ok</Button>
        </div>
      </form>
    </div>
  )
}
